import React, { useState, useEffect } from "react";
import mqtt from "mqtt"; // Install this library with npm install mqtt
import Button from "./Button"; // Import Button component
import Card from "./Card"; // Import Card component
import { FaSolarPanel } from "react-icons/fa"; // Solar Power icon
import { WiRaindrop, WiHumidity, WiThermometer, WiBarometer } from "react-icons/wi"; // Weather icons
import { BsWater } from "react-icons/bs"; // Water drop icon
import "./App.css"; // Import custom CSS styles

const App = () => {
  const [data, setData] = useState({
    solarPower: 0,
    soilMoisture1: 0,
    soilMoisture2: 0,
    temperature: 0,
    humidity: 0,
    pressure: 0,
    gas: 0,
    pumpPower: 0,
    dripperPower: 0,
  });

  const [button1State, setButton1State] = useState(false);
  const [button2State, setButton2State] = useState(false);

  useEffect(() => {
    // Connect to the MQTT broker over TCP
    const client = mqtt.connect({
      host: "192.168.0.4", // Replace with your MQTT broker's hostname or IP
      port: 1883,           // Replace with your broker's TCP port
    });

    const topics = [
      "bme680/temp",
      "bme680/hum",
      "bme680/gas",
      "bme680/pres",
      "soil/sensor1",
      "soil/sensor2",
      "solar/power",
      "solar/pumppower",
      "solar/dripperpower",
      "button1",
      "button2",
    ];

    client.on("connect", () => {
      console.log("Connected to MQTT broker over TCP");
      client.subscribe(topics, (err) => {
        if (err) console.error("Subscription error:", err);
      });
    });

    client.on("message", (topic, message) => {
      try {
        const value = parseFloat(message.toString());
        setData((prevData) => {
          switch (topic) {
            case "bme680/temp":
              return { ...prevData, temperature: value };
            case "bme680/hum":
              return { ...prevData, humidity: value };
            case "bme680/gas":
              return { ...prevData, gas: value };
            case "bme680/pres":
              return { ...prevData, pressure: value };
            case "soil/sensor1":
              return { ...prevData, soilMoisture1: value };
            case "soil/sensor2":
              return { ...prevData, soilMoisture2: value };
            case "solar/power":
              return { ...prevData, solarPower: value };
            case "solar/pumppower":
              return { ...prevData, pumpPower: value };
            case "solar/dripperpower":
              return { ...prevData, dripperPower: value };
            default:
              return prevData;
          }
        });
      } catch (error) {
        console.error("Error parsing MQTT message:", error);
      }
    });

    return () => {
      client.end();
    };
  }, []);

  const handleButton = (button, state) => {
    const topic = button === 1 ? "button1" : "button2";
    const client = mqtt.connect({
      host: "34.29.202.158", // Replace with your broker's hostname or IP
      port: 1883,           // Replace with your broker's TCP port
    });
    client.publish(topic, state ? "ON" : "OFF");
    if (button === 1) {
      setButton1State(state);
    } else {
      setButton2State(state);
    }
    client.end();
  };

  return (
    <div className="app-container">
      <header className="text-center">
        <h1 className="title">Greenhouse Monitoring Dashboard</h1>
      </header>

      <div className="grid-container">
        {/* Solar Power Card */}
        <Card title="Solar Power">
          <FaSolarPanel className="text-yellow-500 text-4xl" />
          <p>{data.solarPower} W</p>
        </Card>

        {/* Soil Moisture 1 Card */}
        <Card title="Soil Moisture 1">
          <WiRaindrop className="text-indigo-500 text-4xl" />
          <p>{data.soilMoisture1}%</p>
        </Card>

        {/* Soil Moisture 2 Card */}
        <Card title="Soil Moisture 2">
          <WiRaindrop className="text-indigo-500 text-4xl" />
          <p>{data.soilMoisture2}%</p>
        </Card>

        {/* Temperature Card */}
        <Card title="Temperature">
          <WiThermometer className="text-red-500 text-4xl" />
          <p>{data.temperature} Â°C</p>
        </Card>

        {/* Humidity Card */}
        <Card title="Humidity">
          <WiHumidity className="text-blue-500 text-4xl" />
          <p>{data.humidity}%</p>
        </Card>

        {/* Pressure Card */}
        <Card title="Pressure">
          <WiBarometer className="text-gray-600 text-4xl" />
          <p>{data.pressure} hPa</p>
        </Card>

        {/* Gas Card */}
        <Card title="Gas">
          <BsWater className="text-teal-500 text-4xl" />
          <p>{data.gas} ppm</p>
        </Card>

        {/* Pump Power Card */}
        <Card title="Pump Power">
          <FaSolarPanel className="text-yellow-500 text-4xl" />
          <p>{data.pumpPower} W</p>
        </Card>

        {/* Dripper Power Card */}
        <Card title="Dripper Power">
          <FaSolarPanel className="text-yellow-500 text-4xl" />
          <p>{data.dripperPower} W</p>
        </Card>

        {/* Button Controls */}
        <div className="button-container">
          <Button onClick={() => handleButton(1, !button1State)}>
            {button1State ? "Turn Off" : "Turn On"} Button 1
          </Button>
          <Button onClick={() => handleButton(2, !button2State)}>
            {button2State ? "Turn Off" : "Turn On"} Button 2
          </Button>
        </div>
      </div>
    </div>
  );
};

export default App;
