import React, { useState, useEffect } from "react";
import axios from "axios";
import { WiSolarEclipse, WiHumidity, WiThermometer, WiBarometer } from "react-icons/wi";
import { FaSeedling, FaWater, FaGasPump, FaPowerOff } from "react-icons/fa";
import { Button } from "@/components/ui/button";
import "./App.css";

const App = () => {
  const [data, setData] = useState({
    solarPower: 0,
    soilMoisture1: 0,
    soilMoisture2: 0,
    temperature: 0,
    humidity: 0,
    pressure: 0,
    gas: 0,
    pumpPower: 0,
    dripperPower: 0,
  });

  const [button1State, setButton1State] = useState(false); // Button 1 ON/OFF
  const [button2State, setButton2State] = useState(false); // Button 2 ON/OFF

  // Fetch data for each sensor
  useEffect(() => {
    const fetchData = async () => {
      try {
        const temperature = await axios.get("http://localhost:5000/temperature");
        const humidity = await axios.get("http://localhost:5000/humidity");
        const gas = await axios.get("http://localhost:5000/gas");
        const pressure = await axios.get("http://localhost:5000/pressure");
        const solarPower = await axios.get("http://localhost:5000/solarPower");
        const soilMoisture1 = await axios.get("http://localhost:5000/soilMoisture1");
        const soilMoisture2 = await axios.get("http://localhost:5000/soilMoisture2");
        const pumpPower = await axios.get("http://localhost:5000/pumpPower");
        const dripperPower = await axios.get("http://localhost:5000/dripperPower");

        setData({
          temperature: temperature.data.temperature,
          humidity: humidity.data.humidity,
          gas: gas.data.gas,
          pressure: pressure.data.pressure,
          solarPower: solarPower.data.solarPower,
          soilMoisture1: soilMoisture1.data.soilMoisture1,
          soilMoisture2: soilMoisture2.data.soilMoisture2,
          pumpPower: pumpPower.data.pumpPower,
          dripperPower: dripperPower.data.dripperPower,
        });
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000); // Refresh every 5 seconds
    return () => clearInterval(interval);
  }, []);

  // Handle Button 1 ON/OFF
  const handleButton1 = async (state) => {
    try {
      await axios.post("http://localhost:5000/buttons", {
        button: 1,
        state: state,
      });
      setButton1State(state);
    } catch (error) {
      console.error("Error sending button1 state:", error);
    }
  };

  // Handle Button 2 ON/OFF
  const handleButton2 = async (state) => {
    try {
      await axios.post("http://localhost:5000/buttons", {
        button: 2,
        state: state,
      });
      setButton2State(state);
    } catch (error) {
      console.error("Error sending button2 state:", error);
    }
  };

  return (
    <div className="app-container">
      <header className="text-center">
        <h1 className="title">Greenhouse Monitoring Dashboard</h1>
      </header>

      <div className="grid-container">
        {/* Sensor Data with Icons */}
        <div className="card">
          <WiSolarEclipse size={50} />
          <h3>Solar Power</h3>
          <p>{data.solarPower} W</p>
        </div>
        <div className="card">
          <FaSeedling size={50} />
          <h3>Soil Moisture 1</h3>
          <p>{data.soilMoisture1} %</p>
        </div>
        <div className="card">
          <FaSeedling size={50} />
          <h3>Soil Moisture 2</h3>
          <p>{data.soilMoisture2} %</p>
        </div>
        <div className="card">
          <WiThermometer size={50} />
          <h3>Temperature</h3>
          <p>{data.temperature} °C</p>
        </div>
        <div className="card">
          <WiHumidity size={50} />
          <h3>Humidity</h3>
          <p>{data.humidity} %</p>
        </div>
        <div className="card">
          <WiBarometer size={50} />
          <h3>Pressure</h3>
          <p>{data.pressure} hPa</p>
        </div>
        <div className="card">
          <FaGasPump size={50} />
          <h3>Gas</h3>
          <p>{data.gas} ppm</p>
        </div>
        <div className="card">
          <FaWater size={50} />
          <h3>Pump Power</h3>
          <p>{data.pumpPower} W</p>
        </div>
        <div className="card">
          <FaWater size={50} />
          <h3>Dripper Power</h3>
          <p>{data.dripperPower} W</p>
        </div>

        {/* Button Controls */}
        <div className="button-container">
          <Button onClick={() => handleButton1(!button1State)}>
            {button1State ? "Turn Off Button 1" : "Turn On Button 1"}
          </Button>
          <Button onClick={() => handleButton2(!button2State)}>
            {button2State ? "Turn Off Button 2" : "Turn On Button 2"}
          </Button>
        </div>
      </div>
    </div>
  );
};

export default App;
